var getMediaTypeExamples = require('../lib/get-mediatype-examples');
/**
 * @param {object} operation
 */
module.exports = function (operation) {
    if (!operation.requestBody || !operation.requestBody.content) {
        return [];
    }
    return Object.keys(operation.requestBody.content || {})
        .map(function (mediaType) {
        var mediaTypeObject = operation.requestBody.content[mediaType];
        var examples = getMediaTypeExamples(mediaType, mediaTypeObject, {
            includeReadOnly: false,
            includeWriteOnly: true
        });
        if (!examples.length) {
            return false;
        }
        return {
            mediaType: mediaType,
            examples: examples
        };
    })
        .filter(Boolean);
};
